package com.aliyun.roompaas.base.tools.retry;import com.aliyun.roompaas.base.IDestroyable;import com.aliyun.roompaas.base.IReset;import com.aliyun.roompaas.base.base.Producer;/** * Created by KyleCe on 2022/5/29 */public interface IRetry<T> extends IReset, IDestroyable {    int DEFAULT_TIMES_LIMIT = 3;    int DEFAULT_INTERVAL_IN_SECONDS = 5;    void config(int retryLimit, int retryIntervalInSeconds, boolean requiresNetwork);    boolean isAbleToRetry();    void setAction(Producer<T> action);    void actionSuccess();    void actionFail();}