package com.aliyun.roompaas.live;import android.Manifest;import android.os.Build;/** * Created by KyleCe on 2022/4/18 */public interface LivePermissionConst {    /**     * 配置要求：     * 1. SDK_INT 版本检查最低使用 R     * 2. AndroidManifest文件中的蓝牙权限声明（不能使用maxSdkVersion配置，否则会判断为未授权）     * <p>     * 测试通过机型：     * mi10-Android12     */    String[] PERMISSIONS_4_LINKER =            Build.VERSION.SDK_INT >= Build.VERSION_CODES.S ?                    new String[]{Manifest.permission.CAMERA                            , Manifest.permission.RECORD_AUDIO                            , Manifest.permission.BLUETOOTH                            , Manifest.permission.BLUETOOTH_CONNECT                    } :                    (Build.VERSION.SDK_INT >= Build.VERSION_CODES.R ?                            new String[]{Manifest.permission.CAMERA                                    , Manifest.permission.RECORD_AUDIO                                    , Manifest.permission.READ_PHONE_STATE                            } :                            new String[]{Manifest.permission.CAMERA                                    , Manifest.permission.RECORD_AUDIO                            });}