package com.aliyun.roompaas.rtc;import com.alivc.rtc.AliRtcEngine;import com.aliyun.roompaas.rtc.exposable.event.RtcStreamEvent;/** * Created by KyleCe on 2022/4/7 */public class RtcStreamEventHelper {    public static RtcStreamEvent asRtcStreamEvent(String userId) {        return new RtcStreamEvent.Builder().setUserId(userId).build();    }    public static RtcStreamEvent asRtcStreamEvent(String userId            , String userName            , boolean isTeacher            , boolean isLocalStream            , AliRtcEngine.AliRtcVideoTrack aliRtcVideoTrack    ) {        return asRtcStreamEvent(userId, userName, isTeacher, isLocalStream, aliRtcVideoTrack, new AliRtcEngine.AliRtcVideoCanvas());    }    public static RtcStreamEvent asRtcStreamEvent(String userId            , String userName            , boolean isTeacher            , boolean isLocalStream            , AliRtcEngine.AliRtcVideoTrack aliRtcVideoTrack            , AliRtcEngine.AliRtcVideoCanvas aliVideoCanvas    ) {        aliVideoCanvas.renderMode = AliRTCManager.DEFAULT_RENDER_MODE;        return new RtcStreamEvent.Builder()                .setUserId(userId)                .setAliRtcVideoTrack(aliRtcVideoTrack)                .setUserName(userName)                .setLocalStream(isLocalStream)                .setTeacher(isTeacher)                .setAliVideoCanvas(aliVideoCanvas)                .build();    }}