package com.aliyun.roompaas.uibase.util;import android.app.Activity;import android.view.View;import android.view.ViewGroup;import com.aliyun.roompaas.base.log.Logger;import com.aliyun.roompaas.base.util.Utils;import com.aliyun.roompaas.uibase.R;import com.aliyun.roompaas.uibase.util.immersionbar.ImmersionBar;import com.aliyun.roompaas.uibase.util.immersionbar.NavigationBarUtils;/** * Created by KyleCe on 2022/5/16 */public class HorizontalMarginAdapter {    public static void adjustIfVital(Activity act, View view, boolean landscape) {        if (view == null || !(view.getLayoutParams() instanceof ViewGroup.MarginLayoutParams) || Utils.isActivityInvalid(act)) {            Logger.e("adjust: end--invalid param: ");            return;        }        ViewGroup.MarginLayoutParams mlp = (ViewGroup.MarginLayoutParams) view.getLayoutParams();        Object obj;        Integer first = (obj = view.getTag(R.integer.viewTagForHorizontalAdjust)) instanceof Integer ? (Integer) obj : null;        if (first == null && landscape) {            view.setTag(R.integer.viewTagForHorizontalAdjust, Math.max(mlp.leftMargin, mlp.rightMargin));        }        int margin = maxHeightOfNavAndStatus(act);        mlp.setMargins(landscape ? margin : (first != null ? first : mlp.leftMargin), mlp.topMargin                , landscape ? margin : (first != null ? first : mlp.rightMargin), mlp.bottomMargin);    }    public static int maxHeightOfNavAndStatus(Activity act) {        final int navHeight = NavigationBarUtils.getNavigationHeight(act);        int statusBarHeight = ImmersionBar.getStatusBarHeight(act);        return Math.max(navHeight, statusBarHeight);    }}